// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
		answer = ifScript();
//
		function ifScript() {

		var check =new GlideRecord('sc_req_item');
		var fc;  
		check.addQuery('request',current.getUniqueValue());
		//check.addQuery('request', current.request);	
		check.setLimit(1);
		check.query();
		if(check.next())
		{
			fc = check.variables.faveur_client;
			if(fc == true ){
				return 'yes';
			}
			return 'no';
		}  
		}

